
public class OddorEven {
	int num=9;
	public void oddEven() {
		
		if(num%2==0) {
			System.out.println("given number is Even");
		}
		else {
			System.out.println("given number is odd");
		}
		}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		OddorEven oe=new OddorEven();
		oe.oddEven();

	}

}
